package hw4;

public class Event {
	public String type;
	public double time;
	
	public Event(String type, double time) {
		// TODO Auto-generated constructor stub
		this.type = type;
		this.time = time;
	}
	
	public String getType() {
		return type;
	}
	
	public double getTime() {
		return time;
	}
	
}